import React, { useState } from "react";
import axios from "axios";
import { GoogleLogin } from "@react-oauth/google";
import "./App.css";
import { jwtDecode } from "jwt-decode";

function App() {
  const [token, setToken] = useState(null);
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);
  const [email, setEmail] = useState(null);

  const handleLoginSuccess = (credentialResponse) => {
    const jwt = credentialResponse.credential;
    const decoded = jwtDecode(jwt);
    setToken(jwt);
    setEmail(decoded.email);
  };

  const handleLoginError = () => {
    alert("Login failed. Please try again.");
  };

  const sendMail = async () => {
    if (!file || !subject || !message || !token) {
      alert("All fields are required!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("subject", subject);
    formData.append("message", message);

    try {
      const res = await axios.post("http://localhost:8081/api/mail/send", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      alert(res.data);
    } catch (err) {
      alert("Failed to send email: " + (err.response?.data || err.message));
    }
  };

  return (
    <div className="container">
      <h1 className="title">ðŸ“§ Gmail Sender App</h1>

      {!token ? (
        <div className="login-box">
          <GoogleLogin onSuccess={handleLoginSuccess} onError={handleLoginError} />
        </div>
      ) : (
        <div className="form-box">
          <p className="welcome-msg">Welcome, {email}</p>
          <label>Subject</label>
          <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} />
          <label>Message</label>
          <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
          <label>Attach a File</label>
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <button onClick={sendMail} className="send-btn">ðŸš€ Send Email</button>
        </div>
      )}
    </div>
  );
}

export default App;
